	public override byte[]? GetFile(string relPath, string pathPrefix, string domain)
		=> relPath switch
		{
			"/2fa.js" => System.Text.Encoding.UTF8.GetBytes($"let code = document.querySelector(\"#code\");\r\n\r\nasync function Continue() {{\r\n    HideError();\r\n    if (code.value === \"\") {{\r\n        ShowError(\"Enter the current code or a recovery code.\");\r\n    }} else {{\r\n        let response = await fetch(\"/api{pathPrefix}/2fa?code=\" + encodeURIComponent(code.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            if (text === \"no\") {{\r\n                ShowError(\"Invalid code.\");\r\n            }} else if (text === \"ok\") {{\r\n                Redirect();\r\n            }} else {{\r\n                ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n    }}\r\n}}"),
			"/auth-request.js" => System.Text.Encoding.UTF8.GetBytes($"async function Allow() {{\r\n    HideError();\r\n    try {{\r\n        var background = GetQuery(\"background\");\r\n        if (background !== \"null\") {{\r\n            var result = await GetReturnAddress(background);\r\n            if (result !== null) {{\r\n                if ((await fetch(result)).status === 200) {{\r\n                    window.location.assign(GetQuery(\"yes\"));\r\n                    return;\r\n                }}\r\n            }}\r\n        }} else {{\r\n            var result = await GetReturnAddress(GetQuery(\"yes\"));\r\n            if (result !== null) {{\r\n                window.location.assign(result);\r\n                return;\r\n            }}\r\n        }}\r\n    }} catch {{\r\n    }}\r\n\r\n    ShowError(\"Connection failed.\");\r\n}}\r\n\r\nasync function GetReturnAddress(address) {{\r\n    try {{\r\n        var response = await fetch(\"/api{pathPrefix}/generate-limited-token?return=\" + encodeURIComponent(address) + \"&name=\" + encodeURIComponent(GetQuery(\"name\")) + \"&allowed=\" + encodeURIComponent(GetQuery(\"allowed\")));\r\n        if (response.status === 200)\r\n            return await response.text();\r\n    }} catch {{\r\n    }}\r\n\r\n    return null;\r\n}}\r\n\r\nfunction GetQuery(q) {{\r\n    try {{\r\n        let query = new URLSearchParams(window.location.search);\r\n        if (query.has(q)) {{\r\n            return query.get(q);\r\n        }} else {{\r\n            return \"null\";\r\n        }}\r\n    }} catch {{\r\n        return \"null\";\r\n    }}\r\n}}"),
			"/login.js" => System.Text.Encoding.UTF8.GetBytes($"let username = document.querySelector(\"#username\");\r\nlet password = document.querySelector(\"#password\");\r\nlet continueButton = document.querySelector(\"#continueButton\").firstElementChild;\r\n\r\nasync function Continue() {{\r\n    HideError();\r\n    if (username.value === \"\") {{\r\n        ShowError(\"Enter your username.\");\r\n    }} else if (password.value === \"\") {{\r\n        ShowError(\"Enter your password.\");\r\n    }} else {{\r\n        continueButton.innerText = \"Loading...\";\r\n        let response = await fetch(\"/api{pathPrefix}/login?username=\" + encodeURIComponent(username.value) + \"&password=\" + encodeURIComponent(password.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            if (text === \"no\") {{\r\n                ShowError(\"Invalid username or password.\");\r\n            }} else if (text === \"ok\") {{\r\n                Redirect();\r\n            }} else {{\r\n                ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n        continueButton.innerText = \"Continue\";\r\n    }}\r\n}}"),
			"/register.js" => System.Text.Encoding.UTF8.GetBytes($"let username = document.querySelector(\"#username\");\r\nlet email = document.querySelector(\"#email\");\r\nlet password1 = document.querySelector(\"#password1\");\r\nlet password2 = document.querySelector(\"#password2\");\r\nlet continueButton = document.querySelector(\"#continueButton\").firstElementChild;\r\n\r\nasync function Continue() {{\r\n    HideError();\r\n    if (username.value === \"\") {{\r\n        ShowError(\"Enter a username.\");\r\n    }} else if (email.value === \"\") {{\r\n        ShowError(\"Enter your email address.\");\r\n    }} else if (password1.value === \"\") {{\r\n        ShowError(\"Enter a password.\");\r\n    }} else if (password2.value === \"\") {{\r\n        ShowError(\"Confirm the password.\");\r\n    }} else if (password1.value != password2.value) {{\r\n        ShowError(\"The passwords do not match.\");\r\n    }} else {{\r\n        continueButton.innerText = \"Loading...\";\r\n        let response = await fetch(\"/api{pathPrefix}/register?username=\" + encodeURIComponent(username.value) + \"&email=\" + encodeURIComponent(email.value) + \"&password=\" + encodeURIComponent(password1.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": Redirect(); break;\r\n                case \"bad-username\": ShowError(\"Usernames must be at least 3 characters long and only contain lowercase letters, digits, dashes, dots and underscores. The first and last characters can only be letters or digits.\"); break;\r\n                case \"bad-password\": ShowError(\"Passwords must be at least 8 characters long and contain at least one uppercase letter, lowercase letter, digit and special character.\"); break;\r\n                case \"bad-email\": ShowError(\"Invalid email address.\"); break;\r\n                case \"username-exists\": ShowError(\"This username is already being used by another account.\"); break;\r\n                case \"email-exists\": ShowError(\"This email address is already being used by another account.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n        continueButton.innerText = \"Continue\";\r\n    }}\r\n}}"),
			"/users-view.js" => System.Text.Encoding.UTF8.GetBytes($"let key = document.querySelector(\"#key\");\r\nlet val = document.querySelector(\"#value\");\r\nlet del = document.querySelector(\"#delete\");\r\nlet access = document.querySelector(\"#access-level\");\r\nlet save = document.querySelector(\"#save\");\r\n\r\nasync function SetSetting(id) {{\r\n    HideError();\r\n    if (key.value === \"\") {{\r\n        ShowError(\"Enter a key.\");\r\n    }} else if (val.value === \"\") {{\r\n        ShowError(\"Enter a value.\");\r\n    }} else {{\r\n        let response = await fetch(\"/api{pathPrefix}/users/set-setting?id=\" + id + \"&key=\" + encodeURIComponent(key.value) + \"&value=\" + encodeURIComponent(val.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.reload(); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n    }}\r\n}}\r\n\r\nasync function DeleteSetting(id, k) {{\r\n    HideError();\r\n    let response = await fetch(\"/api{pathPrefix}/users/delete-setting?id=\" + id + \"&key=\" + encodeURIComponent(k));\r\n    if (response.status === 200) {{\r\n        let text = await response.text();\r\n        switch (text) {{\r\n            case \"ok\": window.location.reload(); break;\r\n            default: ShowError(\"Connection failed.\");\r\n        }}\r\n    }} else {{\r\n        ShowError(\"Connection failed.\");\r\n    }}\r\n}}\r\n\r\nasync function DeleteUser(id) {{\r\n    HideError();\r\n    if (del.firstElementChild.textContent === \"Are you sure?\") {{\r\n        let response = await fetch(\"/api{pathPrefix}/users/delete-user?id=\" + id);\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.assign(\"{pathPrefix}/users\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n    }}\r\n    else {{\r\n        del.firstElementChild.textContent = \"Are you sure?\";\r\n    }}\r\n}}\r\n\r\nasync function SaveAccessLevel(id) {{\r\n    HideError();\r\n    var value = parseInt(access.value);\r\n    if (value == NaN || value < 1 || value > 65535) {{\r\n        ShowError(\"The access level needs to be a number between 1 and 65535!\");\r\n        return;\r\n    }}\r\n    save.className = \"green\";\r\n    save.innerText = \"Saving...\";\r\n    try {{\r\n        switch ((await fetch(\"/api{pathPrefix}/users/set-access-level?id=\" + id + \"&value=\" + access.value)).status) {{\r\n            case 200:\r\n                save.className = \"\";\r\n                save.innerText = \"Saved!\";\r\n                return;\r\n            case 400:\r\n                ShowError(\"The access level needs to be a number between 1 and 65535!\");\r\n                break;\r\n            default:\r\n                ShowError(\"Connection failed.\");\r\n                break;\r\n        }}\r\n    }} catch {{\r\n        ShowError(\"Connection failed.\");\r\n    }}\r\n    save.className = \"green\";\r\n    save.innerText = \"Save\";\r\n}}\r\n\r\nasync function SetAccessLevel(id, value) {{\r\n    access.value = value;\r\n    SaveAccessLevel(id);\r\n}}\r\n\r\nasync function AccessLevelChanged() {{\r\n    save.className = \"green\";\r\n    save.innerText = \"Save\";\r\n}}"),
			"/verify-change.js" => System.Text.Encoding.UTF8.GetBytes($"let code = document.querySelector(\"#email\");\r\n\r\nasync function Continue() {{\r\n    HideError();\r\n    if (code.value === \"\") {{\r\n        ShowError(\"Enter your email address.\");\r\n    }} else {{\r\n        let response = await fetch(\"/api{pathPrefix}/verify-change?email=\" + encodeURIComponent(code.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": Redirect(); break;\r\n                case \"bad\": ShowError(\"Invalid email address.\"); break;\r\n                case \"exists\": ShowError(\"This email address is already being used by another account.\"); break;\r\n                case \"same\": ShowError(\"The provided email address is the same as the old one.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n    }}\r\n}}"),
