			"/recovery/password-request.js" => System.Text.Encoding.UTF8.GetBytes($"let email = document.querySelector(\"#email\");\r\n\r\nasync function Continue(url) {{\r\n    HideError();\r\n    if (email.value === \"\") {{\r\n        ShowError(\"Enter your email address.\");\r\n    }} else {{\r\n        let response = await fetch(\"/api{pathPrefix}/recovery/password?email=\" + encodeURIComponent(email.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.assign(url); break;\r\n                case \"no\": ShowError(\"Invalid email address.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n    }}\r\n}}"),
			"/recovery/password-set.js" => System.Text.Encoding.UTF8.GetBytes($"let password1 = document.querySelector(\"#password1\");\r\nlet password2 = document.querySelector(\"#password2\");\r\n\r\nasync function Continue(url, token) {{\r\n    HideError();\r\n    if (password1.value === \"\") {{\r\n        ShowError(\"Enter a password.\");\r\n    }} else if (password2.value === \"\") {{\r\n        ShowError(\"Confirm the password.\");\r\n    }} else if (password1.value != password2.value) {{\r\n        ShowError(\"The passwords do not match.\");\r\n    }} else {{\r\n        let response = await fetch(\"/api{pathPrefix}/recovery/password?password=\" + encodeURIComponent(password1.value) + \"&token=\" + token);\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.assign(url); break;\r\n                case \"bad\": ShowError(\"Passwords must be at least 8 characters long and contain at least one uppercase letter, lowercase letter, digit and special character.\"); break;\r\n                case \"same\": ShowError(\"The provided password is the same as the old one.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n    }}\r\n}}"),
			"/recovery/username.js" => System.Text.Encoding.UTF8.GetBytes($"let email = document.querySelector(\"#email\");\r\n\r\nasync function Continue(url) {{\r\n    HideError();\r\n    if (email.value === \"\") {{\r\n        ShowError(\"Enter your email address.\");\r\n    }} else {{\r\n        let response = await fetch(\"/api{pathPrefix}/recovery/username?email=\" + encodeURIComponent(email.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.assign(url); break;\r\n                case \"no\": ShowError(\"Invalid email address.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n    }}\r\n}}"),
			"/settings/2fa.js" => System.Text.Encoding.UTF8.GetBytes($"let code = document.querySelector(\"#code\");\r\nlet password = document.querySelector(\"#password\");\r\nlet continueButton = document.querySelector(\"#continueButton\").firstElementChild;\r\n\r\nasync function Continue(method) {{\r\n    HideError();\r\n    if (code.value === \"\") {{\r\n        ShowError(\"Enter the current code or a recovery code.\");\r\n    }} else if (password.value === \"\") {{\r\n        ShowError(\"Enter your password.\");\r\n    }} else {{\r\n        continueButton.innerText = \"Loading...\";\r\n        let response = await fetch(\"/api{pathPrefix}/settings/2fa?method=\" + method + \"&code=\" + encodeURIComponent(code.value) + \"&password=\" + encodeURIComponent(password.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            if (text === \"no\") {{\r\n                ShowError(\"Invalid code or password.\");\r\n            }} else if (text === \"ok\") {{\r\n                window.location.assign(\"{pathPrefix}/settings\");\r\n            }} else {{\r\n                ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n        continueButton.innerText = method === \"enable\" ? \"Enable\" : \"Disable\";\r\n    }}\r\n}}"),
			"/settings/apps.js" => System.Text.Encoding.UTF8.GetBytes($"async function Remove(index, name, expires) {{\r\n    await fetch(\"/api{pathPrefix}/settings/remove-limited-token?index=\" + index + \"&name=\" + name + \"&expires=\" + expires);\r\n    window.location.reload();\r\n}}"),
			"/settings/delete.js" => System.Text.Encoding.UTF8.GetBytes($"let password = document.querySelector(\"#password\");\r\nlet code = document.querySelector(\"#code\");\r\nlet continueButton = document.querySelector(\"#continueButton\").firstElementChild;\r\n\r\nasync function Continue() {{\r\n    HideError();\r\n    if (password.value === \"\") {{\r\n        ShowError(\"Enter your password.\");\r\n    }} else if (code.value === \"\") {{\r\n        ShowError(\"Enter the current code or a recovery code.\");\r\n    }} else {{\r\n        continueButton.innerText = \"Loading...\";\r\n        let response = await fetch(\"/api{pathPrefix}/settings/delete?code=\" + encodeURIComponent(code.value) + \"&password=\" + encodeURIComponent(password.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.assign(\"/\"); break;\r\n                case \"no\": ShowError(\"Invalid password or 2FA code.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n        continueButton.innerText = \"Delete account :(\";\r\n    }}\r\n}}"),
			"/settings/email-verify.js" => System.Text.Encoding.UTF8.GetBytes($"let code = document.querySelector(\"#code\");\r\nlet continueButton = document.querySelector(\"#continueButton\").firstElementChild;\r\n\r\nasync function Continue() {{\r\n    HideError();\r\n    if (code.value === \"\") {{\r\n        ShowError(\"Enter a code.\");\r\n    }} else {{\r\n        continueButton.innerText = \"Loading...\";\r\n        let response = await fetch(\"/api{pathPrefix}/settings/email?code=\" + encodeURIComponent(code.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.assign(\"{pathPrefix}/settings\"); break;\r\n                case \"no\": ShowError(\"Invalid code.\"); break;\r\n                case \"bad\": ShowError(\"Invalid email address.\"); break;\r\n                case \"exists\": ShowError(\"This email address is already being used by another account.\"); break;\r\n                case \"same\": ShowError(\"The provided email address is the same as the old one.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n        continueButton.innerText = \"Change\";\r\n    }}\r\n}}\r\n\r\nasync function Resend() {{\r\n    HideError();\r\n    let response = await fetch(\"/api{pathPrefix}/settings/email?resend=please\");\r\n    if (response.status != 200) {{\r\n        ShowError(\"Connection failed.\");\r\n    }}\r\n}}"),
			"/settings/email.js" => System.Text.Encoding.UTF8.GetBytes($"let email = document.querySelector(\"#email\");\r\nlet password = document.querySelector(\"#password\");\r\nlet code = document.querySelector(\"#code\");\r\nlet continueButton = document.querySelector(\"#continueButton\").firstElementChild;\r\n\r\nasync function Continue() {{\r\n    HideError();\r\n    if (email.value === \"\") {{\r\n        ShowError(\"Enter the email address.\");\r\n    }} else if (password.value === \"\") {{\r\n        ShowError(\"Enter your password.\");\r\n    }} else if (code.value === \"\") {{\r\n        ShowError(\"Enter the current code or a recovery code.\");\r\n    }} else {{\r\n        continueButton.innerText = \"Loading...\";\r\n        let response = await fetch(\"/api{pathPrefix}/settings/email?email=\" + encodeURIComponent(email.value) + \"&code=\" + encodeURIComponent(code.value) + \"&password=\" + encodeURIComponent(password.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.reload(); break;\r\n                case \"no\": ShowError(\"Invalid password or 2FA code.\"); break;\r\n                case \"bad\": ShowError(\"Invalid email address.\"); break;\r\n                case \"exists\": ShowError(\"This email address is already being used by another account.\"); break;\r\n                case \"same\": ShowError(\"The provided email address is the same as the old one.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n        continueButton.innerText = \"Change\";\r\n    }}\r\n}}"),
			"/settings/password.js" => System.Text.Encoding.UTF8.GetBytes($"let password1 = document.querySelector(\"#password1\");\r\nlet password2 = document.querySelector(\"#password2\");\r\nlet password = document.querySelector(\"#password\");\r\nlet code = document.querySelector(\"#code\");\r\nlet continueButton = document.querySelector(\"#continueButton\").firstElementChild;\r\n\r\nasync function Continue() {{\r\n    HideError();\r\n    if (password1.value === \"\") {{\r\n        ShowError(\"Enter a password.\");\r\n    }} else if (password2.value === \"\") {{\r\n        ShowError(\"Confirm the password.\");\r\n    }} else if (password1.value != password2.value) {{\r\n        ShowError(\"The passwords do not match.\");\r\n    }} else if (password.value === \"\") {{\r\n        ShowError(\"Enter your password.\");\r\n    }} else if (code.value === \"\") {{\r\n        ShowError(\"Enter the current code or a recovery code.\");\r\n    }} else {{\r\n        continueButton.innerText = \"Loading...\";\r\n        let response = await fetch(\"/api{pathPrefix}/settings/password?new-password=\" + encodeURIComponent(password1.value) + \"&code=\" + encodeURIComponent(code.value) + \"&password=\" + encodeURIComponent(password.value));\r\n        if (response.status === 200) {{\r\n            let text = await response.text();\r\n            switch (text) {{\r\n                case \"ok\": window.location.assign(\"{pathPrefix}/settings\"); break;\r\n                case \"no\": ShowError(\"Invalid password or 2FA code.\"); break;\r\n                case \"bad\": ShowError(\"Passwords must be at least 8 characters long and contain at least one uppercase letter, lowercase letter, digit and special character.\"); break;\r\n                case \"same\": ShowError(\"The provided password is the same as the old one.\"); break;\r\n                default: ShowError(\"Connection failed.\");\r\n            }}\r\n        }} else {{\r\n            ShowError(\"Connection failed.\");\r\n        }}\r\n        continueButton.innerText = \"Change\";\r\n    }}\r\n}}\r\n\r\nasync function Cancel() {{\r\n    let response = await fetch(\"/api{pathPrefix}/settings/password?action=cancel\");\r\n    if (response.status === 200) {{\r\n        window.location.reload();\r\n    }}\r\n}}"),
